interface Props {
  show: "frontend" | "backend" | "tools" | "others";
}

export default function Skills({ show }: Props) {
  switch (show) {
    case "frontend":
      return (
        <div className={styles.skillsContainer}>
          {frontendSkills.map((skill) => (
            <SkillBox
              key={skill.name}
              skillName={skill.name}
              skillImage={skill.image}
            />
          ))}
        </div>
      );
    case "backend":
      return (
        <div className={styles.skillsContainer}>
          {backendSkills.map((skill) => (
            <SkillBox
              key={skill.name}
              skillName={skill.name}
              skillImage={skill.image}
            />
          ))}
        </div>
      );
    case "tools":
      return (
        <div className={styles.skillsContainer}>
          {tools.map((skill) => (
            <SkillBox
              key={skill.name}
              skillName={skill.name}
              skillImage={skill.image}
            />
          ))}
        </div>
      );
    case "others":
      return (
        <div className={styles.skillsContainer}>
          {others.map((skill) => (
            <SkillBox
              key={skill.name}
              skillName={skill.name}
              skillImage={skill.image}
            />
          ))}
        </div>
      );
  }
}
